import React, { useState, useEffect } from "react";
import "./style.css";
import { BsFillBookmarkFill, BsBookmark } from "react-icons/bs";
import Popup from "reactjs-popup";
import { useHistory } from "react-router-dom";
import Details from "../Details/Details";
import { Link } from "react-router-dom";

function Home() {
  useEffect(() => {
    fetchProducts();
  }, []);

  const [products, setProducts] = useState([]);

  const fetchProducts = async () => {
    const data = await fetch(
      "https://zax5j10412.execute-api.ap-southeast-1.amazonaws.com/dev/api/product/list?page=1"
    );
    const products = await data.json();
    console.log(products.value);
    setProducts(products.value.products);
  };

  const [pressed, setPressed] = useState(false);
  const pressBookmark = () => {
    setPressed((prevState) => !prevState);
  };

  let history = useHistory();

  const clicked = () => {
    history.push(Details);
  };

  return (
    <div className="home">
      <div className="container">
        {products.map((item) => (
          <>
            <div className="card">
              <img classsName="img" key={item.id} src={item.image} />

              <div className="leftSide">
                <div className="topSide">
                  <div className="productName">{item.name}</div>
                  <Link to="/details">
                    <button type="button" onClick={clicked}>
                      See Details
                    </button>
                  </Link>
                  <div>{item.country}</div>
                </div>
                <div>
                  <div>{item.qty < 5 ? item.qty + " left" : null}</div>
                  <div className="price">S$ {item.price}</div>
                  <div className="lowerSide">
                    <Popup
                      trigger={
                        <button className="addCart"> ADD TO CART</button>
                      }
                    >
                      <div> {item.name}Added to cart!</div>
                    </Popup>
                    <Popup
                      trigger={
                        <button onClick={pressBookmark}>
                          {pressed ? (
                            <BsBookmark size="2.5em" color="#cc962c" />
                          ) : (
                            <BsFillBookmarkFill size="2.5em" color="#cc962c" />
                          )}
                        </button>
                      }
                    >
                      <div>Bookmarked!</div>
                    </Popup>
                  </div>
                </div>
              </div>
            </div>
          </>
        ))}
      </div>
    </div>
  );
}

export default Home;
