import React, { useState, useEffect } from "react";
import Header from "../../Shared/Header";
import Popup from "../../Shared/Popup";
import "./style.css";
import { BsFillBookmarkFill, BsBookmark } from "react-icons/bs";

function Details() {
  const [popUp, setPopUp] = useState(false);
  const [bookmark, setBookmark] = useState(false);
  const [details, setDetails] = useState([]);

  const fetchDetails = async () => {
    const data = await fetch(
      "https://zax5j10412.execute-api.ap-southeast-1.amazonaws.com/dev/api/product/263579"
    );
    const details = await data.json();
    console.log(details.value);
    setDetails(details.value);
  };

  useEffect(() => {
    fetchDetails();
  }, []);

  return (
    <div className="mainContainer">
      <Header />
      {details.map((item) => (
        <>
          <div className="detailsContainer">
            <div className="leftContDetail">{item.image}</div>
            <div className="rightContDetail">
              <div>{item.name}</div>
              <div style={{ display: "flex" }}>
                <p>
                  {item.grapeVarieties} {item.vintageYear}
                </p>
                <button
                  className="addCart"
                  onClick={() => {
                    // setSelectedItem(item.name);
                    setPopUp(true);
                  }}
                >
                  ADD TO CART
                </button>
                <Popup trigger={popUp}>
                  <button
                    className="popupclosebtn"
                    onClick={() => setPopUp(false)}
                  >
                    Close
                  </button>
                  {/* {selectedItem} */}
                  added to cart!
                </Popup>

                {/* BOOKMARK */}
                <button
                  onClick={() => {
                    // setSelectedBookmark(item.name);
                    setBookmark(true);
                  }}
                  style={{ border: "none" }}
                >
                  <BsBookmark size="2.5em" color="#cc962c" />
                </button>
                <Popup trigger={bookmark}>
                  <button
                    className="popupclosebtn"
                    onClick={() => setBookmark(false)}
                  >
                    Close
                  </button>
                  {/* {selectedBookmark} */}
                  bookmarked!
                </Popup>
              </div>
              <div className="midDetails">
                <div>
                  <p>Region</p>
                  <p>
                    {item.country} . {item.region}
                  </p>
                </div>
                <div>
                  <p>Producer</p>
                  <p>{item.producer}</p>
                </div>
                <div>
                  <p>Bottle</p>
                  <p>{item.bottleSize} ml</p>
                </div>
                <div>
                  <p>Alcohol</p>
                  <p>{item.alcohol}%</p>
                </div>
              </div>
              <div>
                <p>Description</p>
                <p>{item.description}</p>
              </div>
              <div>
                <p>Tasting Notes</p>
                <p> {item.tastingNotes}</p>
              </div>
            </div>
          </div>
        </>
      ))}
    </div>
  );
}

export default Details;
