import React, { useState } from "react";
import Header from "../../Shared/Header";
import Popup from "../../Shared/Popup";
import "./style.css";
import { BsFillBookmarkFill, BsBookmark } from "react-icons/bs";

function Details() {
  const [popUp, setPopUp] = useState(false);
  const [bookmark, setBookmark] = useState(false);

  const fetchProducts = async () => {
    const data = await fetch(
      "https://zax5j10412.execute-api.ap-southeast-1.amazonaws.com/dev/api/product/list?page=1"
    );
    const products = await data.json();
    console.log(products.value);
    setProducts(products.value.products);
  };

  useEffect(() => {
    fetchProducts().then((res) => {
      window.addEventListener("scroll", (e) => {
        const loaderPosition =
          document.documentElement.scrollTop || document.body.scrollTop;

        const currentHeight = document.documentElement.scrollHeight;
        console.log(currentHeight - loaderPosition);
        if (
          ref.current.children[0].className === "loader" &&
          1053 + loaderPosition === currentHeight
        ) {
          continuousScroll();
        }
      });
    });
  }, [infinityScroll]);

  return (
    <div className="mainContainer">
      <Header />
      <div className="detailsContainer">
        <div className="leftContDetail">img</div>
        <div className="rightContDetail">
          <div>product name</div>
          <div style={{ display: "flex" }}>
            <p>grape varieties year </p>
            <button
              className="addCart"
              onClick={() => {
                // setSelectedItem(item.name);
                setPopUp(true);
              }}
            >
              ADD TO CART
            </button>
            <Popup trigger={popUp}>
              <button className="popupclosebtn" onClick={() => setPopUp(false)}>
                Close
              </button>
              {/* {selectedItem} */}
              added to cart!
            </Popup>

            {/* BOOKMARK */}
            <button
              onClick={() => {
                // setSelectedBookmark(item.name);
                setBookmark(true);
              }}
              style={{ border: "none" }}
            >
              <BsBookmark size="2.5em" color="#cc962c" />
            </button>
            <Popup trigger={bookmark}>
              <button
                className="popupclosebtn"
                onClick={() => setBookmark(false)}
              >
                Close
              </button>
              {/* {selectedBookmark} */}
              bookmarked!
            </Popup>
          </div>
          <div className="midDetails">
            <div>
              <p>Region</p>
              <p>country . region</p>
            </div>
            <div>
              <p>Producer</p>
              <p>producer name</p>
            </div>
            <div>
              <p>Bottle</p>
              <p>ml</p>
            </div>
            <div>
              <p>Alcohol</p>
              <p>percentage</p>
            </div>
          </div>
          <div>
            <p>Description</p>
            <p>desc</p>
          </div>
          <div>
            <p>Tasting Notes</p>
            <p> tasting</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Details;
